import './App.css';
import { useState } from "react"
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';
import Swal from 'sweetalert2'


function App() {

  { /* VARIABLES USUARIO */ }

  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [username, setUsername] = useState("");
  const [clave, setClave] = useState("");

  { /* VARIABLES MARCA */  }

  const [marca, setMarca] = useState("");
  const [paisOrigen, setPaisOrigen] = useState("");



  const [editar, setEditar] = useState(false);
  const [id, setId] = useState();
  const [userList, setUser] = useState([]);
  const [marcaList, setMarcaList] = useState([]);

  const [mostrarLista, setMostrarLista] = useState(false);

  { /* FUNCIONES USUARIO */ }
  const agregar = () => {
    axios.post("http://localhost:3001/registrar", {
      nombre: nombre,
      apellido: apellido,
      username: username,
      clave: clave
    }).then(() => {
      Swal.fire({
        title: "¡Usuario registrado con éxito!",
        icon: "success",
      });

      limpiar();
    });
  }

  const update = () => {
    axios.put("http://localhost:3001/update", {
      id: id,
      nombre: nombre,
      apellido: apellido,
      username: username,
      clave: clave
    }).then(() => {
      getUser();
      alert("Usuario actualizado con éxito!");
      limpiar();
    });
  }

  const limpiar = () => {
    setNombre("");
    setApellido("");
    setUsername("");
    setClave("");
  }

  const cancelar = () => {
    setEditar(false);
    limpiar();
  }

  const editUser = (val) => {
    setEditar(true);
    setNombre(val.nombre);
    setApellido(val.apellido);
    setUsername(val.username);
    setClave(val.clave);
    setId(val.id);
  }

  const getUser = (req, res) => {
    axios.get("http://localhost:3001/usuario").then((response) => {
      setUser(response.data);
    });
}

const toggleList = () => {
    if (!mostrarLista) {
      getUser(); // Carga los datos al abrir
    }
    setMostrarLista(!mostrarLista);
  }

  const deleteUser = (id) => {

    Swal.fire({
        title: "Are you sure?",
        text: "You won't be able to revert this!",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, delete it!"
      }).then((res) => {
        if (res.isConfirmed) {
          axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            getUser();
            limpiar();
          });
          Swal.fire({
            title: "Deleted!",
            text: "Your file has been deleted.",
            icon: "success"
          });
        }
      });
  }

  { /* FUNCIONES MARCA */ }
const agregarMarca = () => {
    axios.post("http://localhost:3001/registrarMarca", {
      marca: marca,
      paisOrigen: paisOrigen
    }).then(() => {
      Swal.fire({
        title: "¡Marca registrada con éxito!",
        icon: "success",
      });

      limpiarMarca();
    });
  }

  const updateMarca = () => {
    axios.put("http://localhost:3001/updateMarca", {
      id: id,
      marca: marca,
      paisOrigen: paisOrigen
    }).then(() => {
      getMarca();
      alert("Marca actualizada con éxito!");
      limpiarMarca();
    });
  }

  const limpiarMarca = () => {
    setMarca("");
    setPaisOrigen("");
  }

  const cancelarMarca = () => {
    setEditar(false);
    limpiarMarca();
  }

  const editMarca = (val) => {
    setEditar(true);
    setMarca(val.marca);
    setPaisOrigen(val.paisOrigen);
    setId(val.id);
  }

  const getMarca = (req, res) => {
    axios.get("http://localhost:3001/marca").then((response) => {
      setMarcaList(response.data);
    });
}

const toggleListMarca = () => {
    if (!mostrarLista) {
      getMarca(); // Carga los datos al abrir
    }
    setMostrarLista(!mostrarLista);
  }

  const deleteMarca = (id) => {

    Swal.fire({
        title: "Are you sure?",
        text: "You won't be able to revert this!",
        icon: "warning",
        showCancelButton: true,
        confirmButtonColor: "#3085d6",
        cancelButtonColor: "#d33",
        confirmButtonText: "Yes, delete it!"
      }).then((res) => {
        if (res.isConfirmed) {
          axios.delete(`http://localhost:3001/delete/${id}`).then((response) => {
            getMarca();
            limpiarMarca();
          });
          Swal.fire({
            title: "Deleted!",
            text: "Your file has been deleted.",
            icon: "success"
          });
        }
      });
  }

  return (
    <div className="container">
      <div className='card-group'>

      { /* REGISTRO DE USUARIOS */ }
      <div className='registro-usuarios'>
        <div className="card text-center">
        <div className="card">
          REGISTRO DE USUARIOS
        </div>
        <div className="card-body">
          { /* label nombre */ }
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1"> Nombre: </span>
          <input onChange={(e) => {setNombre(e.target.value);}} type="text" value={nombre} className="form-control" placeholder="ingrese su nombre" aria-label="nombre" aria-describedby="basic-addon1"/>
        </div>

          { /* label apellido */}
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1"> Apellido: </span>
          <input onChange={(e) => {setApellido(e.target.value);}} type="text" value={apellido} className="form-control" placeholder="ingrese su apellido" aria-label="apellido" aria-describedby="basic-addon1"/>
        </div>

        { /* label username */}
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1"> Username: </span>
          <input onChange={(e) => {setUsername(e.target.value);}} type="text" value={username} className="form-control" placeholder="ingrese su nombre de usuario" aria-label="username" aria-describedby="basic-addon1"/>
        </div>

        { /* label clave */}
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1"> Clave: </span>
          <input onChange={(e) => {setClave(e.target.value);}} type="text" value={clave} className="form-control" placeholder="ingrese su contraseña" aria-label="username" aria-describedby="basic-addon1"/>
        </div>

        </div>
        <div className="card-footer text-muted">
        {
          editar?
          <div className="btn-group" role="group" aria-label="Basic outlined example">
            <button type="button" className="btn btn-danger" 
               onClick={update} >Actualizar Usuario</button>
            <button type="button" className="btn btn-outline-danger" onClick={cancelar}>Cancelar</button>
           </div>
          : <button className='btn btn-success' onClick={agregar} >Registrar Usuario</button>
        }
        </div>
      </div>

      <div className="mb-3">
        <button 
          onClick={toggleList} 
          className={mostrarLista ? "btn btn-secondary" : "btn btn-primary"} >
          {mostrarLista ? "Cerrar Lista" : "Listar Usuarios"}
        </button>
      </div>
      </div>

    { /* REGISTRO DE MARCAS */ }
      <div className='registro-marca'>
        <div className="card text-center">
        <div className="card">
          REGISTRO DE MARCAS
        </div>
        <div className="card-body">
          { /* label marca */}
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1"> Marca: </span>
          <input onChange={(e) => {setMarca(e.target.value);}} type="text" value={marca} className="form-control" placeholder="ingrese la marca" aria-label="marca" aria-describedby="basic-addon1"/>
        </div>

          { /* label país de origen */}
        <div className="input-group mb-3">
          <span className="input-group-text" id="basic-addon1"> País de Origen: </span>
          <input onChange={(e) => {setPaisOrigen(e.target.value);}} type="text" value={paisOrigen} className="form-control" placeholder="ingrese el país de origen" aria-label="paisOrigen" aria-describedby="basic-addon1"/>
        </div>
        <div className="card-footer text-muted">
        <button className='btn btn-success' onClick={agregarMarca} >Registrar Marca</button>
        </div>
      </div>
      </div>
      </div>
      </div>

      {mostrarLista && (
      <table className="table table-striped table-hover">
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Nombre</th>
            <th scope="col">Apellido</th>
            <th scope="col">Username</th>
            <th scope="col">Clave</th>
            <th scope="col">Acciones</th>
          </tr>
        </thead>

        <tbody>
        {
          userList.map((val, key) => {
            return <tr key={val.id}>
                <th>{val.id}</th>
                <td>{val.nombre}</td>
                <td>{val.apellido}</td>
                <td>{val.username}</td>
                <td>{val.clave}</td>
                <td>
                  <div className="btn-group" role="group" aria-label="Basic outlined example">
                    <button type="button" 
                    onClick={() => {editUser(val)}} 
                    className="btn btn-outline-primary">Editar</button>
                    <button type="button" onClick={() => {deleteUser(val.id)}} className="btn btn-danger">Eliminar</button>
                  </div>
                </td>
              </tr>
          })
        }
        </tbody>
          </table>
      )}
    </div>

  );
}

export default App;
