import './App.css';
import { useState } from "react"
import axios from "axios";
import 'bootstrap/dist/css/bootstrap.min.css';


function App() {

  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [username, setUsername] = useState("");
  const [clave, setClave] = useState("");

  const [userList, setUser] = useState([]);
  const agregar = () => {
    axios.post("http://localhost:3001/registrar", {
      nombre: nombre,
      apellido: apellido,
      username: username,
      clave: clave
    }).then(() => {
      alert("Usuario registrado con éxito!");
    });
  }

  const getUser = (req, res) => {
    axios.get("http://localhost:3001/usuario").then((response) => {
      setUser(response.data);
    });
}

  return (
    <div class="container">
      <div className="App">
      <div className="Lista">
      <button onClick={getUser} >Listar Usuarios</button>
      {
        userList.map((val, key) => {
          return <div key={key}>
            <p>Nombre: {val.nombre}</p>
            <p>Apellido: {val.apellido}</p>
            <p>Username: {val.username}</p>
            <p>Clave: {val.clave}</p>
          </div>
        })
      }
      </div>
      </div>
        <div class="card text-center">
        <div class="card-header">
          REGISTRO DE USUARIOS
        </div>
        <div class="card-body">
        <div class="input-group mb-3">
          <span class="input-group-text" id="basic-addon1">@</span>
          <input type="text" class="form-control" placeholder="Username" aria-label="Username" aria-describedby="basic-addon1"/>
        </div>
        <label>Nombre: <input onChange={(e) => {setNombre(e.target.value);}} type="text"/></label>
        <label>Apellido: <input onChange={(e) => {setApellido(e.target.value);}} type="text"/></label>
        <label>Username: <input onChange={(e) => {setUsername(e.target.value);}} type="text"/></label>
        <label>Clave: <input onChange={(e) => {setClave(e.target.value);}} type="text"/></label>

        </div>
        <div class="card-footer text-muted">
        <button className='btn btn-success' onClick={agregar} >Registrar Usuario</button>
        </div>
      </div>
    </div>
  );
}

export default App;
