import './App.css';
import { useState } from "React"

function App() {

  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [username, setUsername] = useState("");
  const [clave, setClave] = useState("");
  const mostrar = () => {
    alert("Se ha registrado el usuario con los siguientes datos: " + "\n" + "Nombre: " + nombre + "\n" + "Apellido: " + apellido + "\n" + "Username: " + username + "\n" + "Clave: " + clave);
  }


  return (
    <div className="App">
      <div className="datos">
        <label>Nombre: <input onChange={(e) => {setNombre(e.target.value);}} type="text"/></label>
        <label>Apellido: <input onChange={(e) => {setApellido(e.target.value);}} type="text"/></label>
        <label>Username: <input onChange={(e) => {setUsername(e.target.value);}} type="text"/></label>
        <label>Clave: <input onChange={(e) => {setClave(e.target.value);}} type="text"/></label>
        <button>Registrar Usuario</button>
      </div>
    </div>
  );
}

export default App;
