import './App.css';
import { useState } from "react"
import axios from "axios";


function App() {

  const [nombre, setNombre] = useState("");
  const [apellido, setApellido] = useState("");
  const [username, setUsername] = useState("");
  const [clave, setClave] = useState("");
  const agregar = () => {
    axios.post("http://localhost:3001/registrar", {
      nombre: nombre,
      apellido: apellido,
      username: username,
      clave: clave
    }).then(() => {
      alert("Usuario registrado con éxito!");
    });
  }

  const getUsuario = (req, res) => {
    Axios.get("http://localhost:3001/usuario").then((response) => {
        res.send(response.data);
    });
}

  return (
    <div className="App">
      <div className="datos">
        <label>Nombre: <input onChange={(e) => {setNombre(e.target.value);}} type="text"/></label>
        <label>Apellido: <input onChange={(e) => {setApellido(e.target.value);}} type="text"/></label>
        <label>Username: <input onChange={(e) => {setUsername(e.target.value);}} type="text"/></label>
        <label>Clave: <input onChange={(e) => {setClave(e.target.value);}} type="text"/></label>
        <button onClick={agregar} >Registrar Usuario</button>
      </div>
    </div>
  );
}

export default App;
